CREATE OR REPLACE FUNCTION obter_se_ordem_reprovada_atx (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_reprovacao_w			varchar(1)	:= 'N';
qt_reprovacao_w			integer;
qt_item_w			integer;
BEGIN
select 	count(*)
into STRICT	qt_reprovacao_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	(nr_seq_aprovacao IS NOT NULL AND nr_seq_aprovacao::text <> '')
and	(dt_reprovacao IS NOT NULL AND dt_reprovacao::text <> '');

select 	count(*)
into STRICT	qt_item_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p;

if (qt_reprovacao_w = qt_item_w) then
	ie_reprovacao_w	:= 'S';
end if;


return	ie_reprovacao_w;

end;
