CREATE OR REPLACE FUNCTION obter_se_ordem_total_solic (nr_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1) := 'N';
qt_itens_solic_w	bigint;
qt_itens_solic_ordem_w	bigint;
		

BEGIN 
select	count(*) 
into STRICT	qt_itens_solic_w 
from	solic_compra_item 
where	nr_solic_compra = nr_solic_compra_p;
 
select	count(*) 
into STRICT	qt_itens_solic_ordem_w 
from	solic_compra_item 
where	nr_solic_compra = nr_solic_compra_p 
and	(obter_ordem_solic_compra(nr_solic_compra,nr_item_solic_compra) IS NOT NULL AND (obter_ordem_solic_compra(nr_solic_compra,nr_item_solic_compra))::text <> '');
 
if (qt_itens_solic_w = qt_itens_solic_ordem_w) then 
  ds_retorno_w := 'S';
end if;
 
return	ds_retorno_w;
 
end;
$body$
