CREATE OR REPLACE FUNCTION obter_se_ordem_vinc_projeto2 ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'N';
ie_adicional_w		varchar(01) := 'N';
qt_projeto_w		bigint;


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_adicional_w
from	proj_projeto_cliente_adic
where	NR_SEQ_ORDEM_SERV 	= nr_sequencia_p;

if (ie_adicional_w = 'N') then
	begin

	select  count(*)
	into STRICT	qt_projeto_w
	from    proj_projeto
	where   nr_seq_ordem_serv 	= nr_sequencia_p;

	if (qt_projeto_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
	end;
else
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
