CREATE OR REPLACE FUNCTION obter_se_orient_alta_rn (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) :=  'N';
ie_lib_w		varchar(1) :=	'N';
ie_rn_lib_w		varchar(1) :=	'N';
ie_sexo_w		varchar(1);
ie_atend_rn_w		varchar(1);
qt_nascimento           bigint;


BEGIN


if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

select	max(ie_sexo)
into STRICT	ie_sexo_w
from	atendimento_paciente a,
	pessoa_fisica b
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
and	a.nr_atendimento = nr_atendimento_p;


select count(*)
into STRICT qt_nascimento
from nascimento
where nr_atendimento = nr_atendimento_p;

	if (ie_sexo_w <> 'F') or (qt_nascimento = 0)  then
		ds_retorno_w := 'S';
	else
		begin
				select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
				into STRICT   	ie_lib_w
				from   	atendimento_alta b, parametro_medico p
				where  	nr_atendimento = nr_atendimento_p
				and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and		coalesce(ie_recem_nato,'N') = 'N'
				and		coalesce(ie_situacao,'A') = 'A'
				and     p.cd_estabelecimento = obter_estabelecimento_ativo
				and     ((b.ie_tipo_orientacao <> 'P')
				or (coalesce(p.ie_liberar_desfecho,'N')  = 'N')
				or      ((b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') and (coalesce(b.dt_inativacao::text, '') = '')));

				select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
				into STRICT   	ie_rn_lib_w
				from   	atendimento_alta b, parametro_medico p
				where  	nr_atendimento = nr_atendimento_p
				and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and		coalesce(ie_recem_nato,'N') = 'S'
				and		coalesce(ie_situacao,'A') = 'A'
				and     p.cd_estabelecimento = obter_estabelecimento_ativo
				and     ((b.ie_tipo_orientacao <> 'P')
				or (coalesce(p.ie_liberar_desfecho,'N')  = 'N')
				or      ((b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') and (coalesce(b.dt_inativacao::text, '') = '')));


			if (ie_lib_w = 'S') and (ie_rn_lib_w = 'S') then

				ds_retorno_w := 'S';

			end if;


		end;
	end if;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
