CREATE OR REPLACE FUNCTION obter_se_os_analise_critica (nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select  coalesce(max('S'),'N')
into STRICT	ds_retorno_w
from 	PROJ_ATA e,
	PROJ_ATA_PENDENCIA c,
	PROJ_ATA_PENDENCIA_OS d,
	ata_reuniao f
where 	d.NR_SEQ_PENDENCIA = c.nr_sequencia
and	c.nr_seq_ata = e.nr_sequencia
and	e.nr_seq_reuniao		= f.nr_sequencia
and   	nr_seq_classif_reuniao = 3
and	d.nr_seq_ordem = nr_seq_ordem_p  LIMIT 1;

return	ds_retorno_w;

end;
