CREATE OR REPLACE FUNCTION obter_se_os_antiga ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			bigint;

BEGIN

select	CASE WHEN obter_se_data_periodo(trunc(a.dt_ordem_servico, 'dd'), to_date('01/01/2007','dd/mm/yyyy'), trunc(clock_timestamp() - interval '15 days','dd'))='S' THEN  1  ELSE 0 END
into STRICT	qt_retorno_w
from	man_ordem_servico a
where	a.nr_sequencia	= nr_sequencia_p;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
