CREATE OR REPLACE FUNCTION obter_se_os_gerar_nec_migr (nr_seq_os_p bigint) RETURNS varchar AS $body$
DECLARE


ie_necessidade_w		varchar(1) := 'N';
ie_customizacao_w		varchar(15);
ie_situacao_w		varchar(1);
nr_seq_grupo_des_w	bigint;


BEGIN
if (nr_seq_os_p IS NOT NULL AND nr_seq_os_p::text <> '') then
	begin
	select	ie_tipo_customizacao_os,
		obter_situacao_funcao_migracao(cd_funcao),
		coalesce(nr_seq_grupo_des, 0)
	into STRICT	ie_customizacao_w,
		ie_situacao_w,
		nr_seq_grupo_des_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_os_p;

	if (ie_customizacao_w in ('F','0','C')) and
		((ie_situacao_w in ('W','T','A')) or (nr_seq_grupo_des_w in (31,32))) and (nr_seq_grupo_des_w not in (7, 33)) then
		begin
		ie_necessidade_w := 'S';
		end;
	end if;
	end;
end if;
return ie_necessidade_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
