CREATE OR REPLACE FUNCTION obter_se_os_teste_usuario ( nr_seq_os_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_os_teste_w		varchar(1) := 'N';
cd_pessoa_usuario_w	varchar(10);


BEGIN
if (nr_seq_os_p IS NOT NULL AND nr_seq_os_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_usuario_w
	from	usuario
	where	nm_usuario = nm_usuario_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_os_teste_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_os_p
	and	((cd_pessoa_solicitante = cd_pessoa_usuario_w)
	or (nm_usuario_exec = nm_usuario_p)
	or (nm_usuario_nrec = nm_usuario_p))
	and	nr_seq_estagio in (2,41,1511);
	end;
end if;
return ie_os_teste_w;
end;
