CREATE OR REPLACE FUNCTION obter_se_paciente_acessorio ( cd_pessoa_fisica_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_acessorio_w	bigint;
cont_w			bigint;
ie_retorno_w		varchar(10) := 'N';

c01 CURSOR FOR
SELECT	b.nr_seq_acessorio
from	acessorio_pac_proced b
where	b.cd_procedimento	= cd_procedimento_p
and	b.ie_origem_proced	= ie_origem_proced_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_acessorio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	count(*)
	into STRICT	cont_w
	from	paciente_acessorio
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	nr_seq_acessorio	= nr_seq_acessorio_w
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	if (cont_w > 0) then
		ie_retorno_w	:= 'S';
		exit;
	end if;

end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
