CREATE OR REPLACE FUNCTION obter_se_paciente_agendado ( cd_pessoa_fisica_p text, cd_agenda_p bigint, dt_agenda_p timestamp, ie_agenda_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
			Agenda 
Agenda de consulta		C 
 
			Opção 
Nome da agenda		N 
Código da agenda		C			 
*/
					 
					 
ds_retorno_w 		varchar(255) := 'N';
cd_agenda_w		bigint;
nm_agenda_w		varchar(4000);

				 

BEGIN 
 
If (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then	 
 
		 
	If (ie_agenda_p = 'C') then 
 
		Select 	coalesce(max(cd_agenda),0) 
		into STRICT	cd_agenda_w 
		from	agenda_consulta 
		where	cd_pessoa_fisica = cd_pessoa_fisica_p 
		and	dt_agenda = dt_agenda_p 
		and	ie_status_agenda not in ('C','F','I') 
		and	cd_agenda <> cd_agenda_p;
		 
		if (cd_agenda_w > 0) then 
		 
			select 	substr(obter_nome_pf(cd_pessoa_fisica) || CASE WHEN coalesce(DS_CURTA::text, '') = '' THEN ''  ELSE ' ('||DS_CURTA||')' END ,1,120)||' ('||ds_complemento||')' 
			into STRICT	nm_agenda_w 
			from	agenda 
			where	cd_agenda = cd_agenda_w;			
						 
			if (ie_opcao_p = 'N') then 
				 
				ds_retorno_w := substr(nm_agenda_w,1,255);
				 
			else 
			 
				ds_retorno_w := cd_agenda_w;
			 
			end if;
			 
		end if;
 
	end if;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
