CREATE OR REPLACE FUNCTION obter_se_paciente_apto_medic (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_apto_w	timestamp;
ie_apto_w	varchar(1);


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
	select    max(dt_apto)
	into STRICT      dt_apto_w
	from      paciente_atendimento
	where     nr_seq_atendimento = nr_seq_atendimento_p;

	if (dt_apto_w IS NOT NULL AND dt_apto_w::text <> '') then
		ie_apto_w := 'S';
	else
		ie_apto_w := 'N';
	end if;
end if;

