CREATE OR REPLACE FUNCTION obter_se_paciente_atend ( cd_pessoa_fisica_p text, cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);
ie_possui_atend_w	varchar(1);
ds_agenda_w		varchar(255);
	
			 

BEGIN 
ie_possui_atend_w := 'N';
	 
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_possui_atend_w 
from	agenda_consulta 
where	ie_status_agenda = 'O' 
and 	cd_pessoa_fisica = cd_pessoa_fisica_p 
and		trunc(dt_agenda,'dd') = trunc(dt_agenda_p) 
and		cd_agenda <> cd_agenda_p;
 
if (ie_possui_atend_w = 'S') then 
	begin 
	select	substr(obter_desc_agenda(max(cd_agenda)),1,255) 
	into STRICT	ds_agenda_w 
	from	agenda_consulta 
	where	ie_status_agenda = 'O' 
	and 	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and		trunc(dt_agenda) = trunc(dt_agenda_p) 
	and		cd_agenda <> cd_agenda_p;
	 
	ds_retorno_w := obter_texto_tasy(256120, wheb_usuario_pck.get_nr_seq_idioma) || ' ' || ds_agenda_w;
	end;
else 
	ds_retorno_w := '';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
