CREATE OR REPLACE FUNCTION obter_se_paciente_cir_recup ( nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p text, ds_param_74_ocup_p text default null) RETURNS varchar AS $body$
DECLARE

 
ds_resultado_w		varchar(1);
qt_quantidade_w		integer;
ds_parametro_w		varchar(5);


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
ds_resultado_w	:= 'N';
 
if (coalesce(ds_param_74_ocup_p::text, '') = '') then 
	ds_parametro_w := Obter_Param_Usuario(44, 74, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ds_parametro_w);
else 
	ds_parametro_w := ds_param_74_ocup_p;
end if;
 
if (ds_parametro_w = 'N') then 
	begin 
	select	count(*) 
	into STRICT 	qt_quantidade_w 
	from	cirurgia 
	where	nr_atendimento = nr_atendimento_p 
	and 	(dt_entrada_recup IS NOT NULL AND dt_entrada_recup::text <> '') 
	and 	coalesce(dt_saida_recup::text, '') = '';
 
	if (qt_quantidade_w > 0) then 
		ds_resultado_w	:= 'S';
	end if;
	end;
else 
	begin 
	select	count(*) 
	into STRICT 	qt_quantidade_w 
	from	agenda_paciente 
	where	nr_atendimento = nr_atendimento_p 
	and 	(dt_sala_recuperacao IS NOT NULL AND dt_sala_recuperacao::text <> '') 
	and		coalesce(dt_alta_cc::text, '') = '' 
	and   ie_status_agenda in ('N','R','E') 
	and 	obter_tipo_agenda(cd_agenda) = 1;
 
	if (qt_quantidade_w > 0) then 
		ds_resultado_w	:= 'S';
	end if;
	end;
end if;
end if;
 
return ds_resultado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
