CREATE OR REPLACE FUNCTION obter_se_paciente_controle ( nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from 	cih_controle_atend z
	where 	z.nr_atendimento = nr_atendimento_p
	and	coalesce(dt_finalizacao::text, '') = '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
