CREATE OR REPLACE FUNCTION obter_se_paciente_doc_exame (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE

nr_seq_agenda_int_w bigint;

BEGIN

SELECT	max(a.nr_seq_agenda_int)
INTO STRICT	nr_seq_agenda_int_w
FROM	agenda_integrada_item a,
	agenda_paciente b
WHERE	a.nr_seq_agenda_exame = b.nr_sequencia
AND	b.cd_pessoa_fisica = cd_pessoa_fisica_p
AND	b.dt_agenda = TRUNC(clock_timestamp());

return	coalesce(nr_seq_agenda_int_w,0);

end;
