CREATE OR REPLACE FUNCTION obter_se_paciente_fumante_pep (nr_atendimento_p bigint, cd_pessoa_fisica_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	cd_pessoa_fisica_w := cd_pessoa_fisica_p;
elsif (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	obter_pessoa_atendimento(nr_atendimento_p,'C')
	into STRICT	cd_pessoa_fisica_w
	;
end if;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then	
	
	Select CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT   ds_retorno_w
	from   paciente_habito_vicio a
	where  a.cd_pessoa_fisica = cd_pessoa_fisica_w
	and    coalesce(a.dt_fim,clock_timestamp()) >= clock_timestamp() 
	and    (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	   (a.nr_seq_tipo IS NOT NULL AND a.nr_seq_tipo::text <> '')
	and    coalesce(a.dt_inativacao::text, '') = ''
	and		exists ( SELECT 1 
					from 	med_tipo_vicio b
					where 	b.nr_sequencia = a.nr_seq_tipo
					and		coalesce(b.IE_FUMANTE,'N') = 'S');		

end if;

return	ds_retorno_w;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
