CREATE OR REPLACE FUNCTION obter_se_paciente_grupo ( cd_pessoa_fisica_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10)	:= 'N';
qt_reg_w		bigint;

BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	count(*)
	into STRICT	qt_reg_w
	from	pac_grupo_atend
	where	nr_atendimento	= nr_atendimento_p
	and		coalesce(DT_SAIDA::text, '') = '';

	if (qt_reg_w	> 0) then
		ds_retorno_w	:= 'S';
	end if;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	count(*)
	into STRICT	qt_reg_w
	from	pac_grupo_atend
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		coalesce(nr_atendimento::text, '') = ''
	and		coalesce(DT_SAIDA::text, '') = '';

	if (qt_reg_w	> 0) then
		ds_retorno_w	:= 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
