CREATE OR REPLACE FUNCTION obter_se_paciente_hc (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

ie_paciente_hc	varchar(10);


BEGIN
select 	count(*)
into STRICT	ie_paciente_hc
from	paciente_home_care
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (ie_paciente_hc > 0) then
	ie_paciente_hc := 'S';

else	ie_paciente_hc := 'N';
end if;

return	ie_paciente_hc;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
