CREATE OR REPLACE FUNCTION obter_se_paciente_mi (CD_PESSOA_FISICA_P text) RETURNS varchar AS $body$
DECLARE


DS_RETORNO_W	varchar(1);


BEGIN

SELECT  coalesce(MAX('S'), 'N')
INTO STRICT	DS_RETORNO_W
FROM    CIH_LOCAL_INFECCAO A,
	CIH_FICHA_OCORRENCIA B
WHERE   A.NR_FICHA_OCORRENCIA = B.NR_FICHA_OCORRENCIA
AND	B.NR_ATENDIMENTO = (	SELECT	MAX(C.NR_ATENDIMENTO)
				FROM	ATENDIMENTO_PACIENTE C
				WHERE	CD_PESSOA_FISICA_P = C.CD_PESSOA_FISICA)
AND     A.IE_DOENCA_INFECTOCONTAGIOSA = 'S';

RETURN	DS_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
