CREATE OR REPLACE FUNCTION obter_se_paciente_quimio (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_quimio_w	bigint;
ie_quimio_w	varchar(1);


BEGIN

ie_quimio_w	:= 'N';

if (nr_atendimento_P IS NOT NULL AND nr_atendimento_P::text <> '') then
	select	count(*)
	into STRICT	qt_quimio_w
	from	material c,
		prescr_material b,
		prescr_medica a
	where	c.ie_mistura		= 'S'
	and	c.cd_material		= b.cd_material
	and	b.nr_prescricao	= a.nr_prescricao
	and	a.nr_atendimento	= nr_atendimento_p;

	if (qt_quimio_w > 0) then
		ie_quimio_w	:= 'S';
	end if;
end if;

return	ie_quimio_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
