CREATE OR REPLACE FUNCTION obter_se_paciente_tratamento (cd_pessoa_fisica_p text, ie_tratamento_p text) RETURNS varchar AS $body$
DECLARE


ie_tratamento_w	varchar(1);
qt_1_w		integer;
qt_2_w		integer;
qt_total_w	integer;


BEGIN

select 	count(*)
into STRICT	qt_1_w
from	paciente_tratamento
where	ie_tratamento = ie_tratamento_p
and	ie_tratamento_p not in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
and	cd_pessoa_fisica = cd_pessoa_fisica_p
and	coalesce(dt_final_tratamento::text, '') = '';

select 	count(*)
into STRICT	qt_2_w
from	paciente_tratamento
where	ie_tratamento = ie_tratamento_p
and	ie_tratamento_p in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
and	cd_pessoa_fisica = cd_pessoa_fisica_p
and	coalesce(dt_final_tratamento::text, '') = '';


qt_total_w := (qt_1_w+qt_2_w);

select 	CASE WHEN qt_total_w=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_tratamento_w
;

return ie_tratamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
