CREATE OR REPLACE FUNCTION obter_se_pacote_convenio_gv (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_gestao_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_pacote_w		varchar(1):= 'N';
qt_pacote_w		bigint:=0;
cd_estabelecimento_w	smallint;	
cd_convenio_w		bigint;
				

BEGIN 
 
qt_pacote_w:= 0;
 
if (coalesce(cd_procedimento_p,0) > 0) and (coalesce(ie_origem_proced_p,0) > 0) and (coalesce(nr_seq_gestao_p,0) > 0) then 
	 
	select	max(cd_estabelecimento), 
		max(cd_convenio) 
	into STRICT	cd_estabelecimento_w, 
		cd_convenio_w 
	from	gestao_vaga 
	where	nr_sequencia = nr_seq_gestao_p;
	 
	select 	count(*) 
	into STRICT	qt_pacote_w 
	from 	pacote 
	where	cd_proced_pacote = cd_procedimento_p 
	and 	ie_origem_proced = ie_origem_proced_p 
	and 	cd_convenio	 = cd_convenio_w 
	and 	cd_estabelecimento = cd_estabelecimento_w 
	and 	ie_situacao = 'A';
end if;
 
if (qt_pacote_w > 0) then 
	ie_pacote_w:= 'S';
else 
	ie_pacote_w:= 'N';
end if;
 
return	ie_pacote_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
