CREATE OR REPLACE FUNCTION obter_se_pacote_convenio (cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pacote_w	varchar(1):= 'N';
qt_pacote_w	bigint:=0;


BEGIN

qt_pacote_w:= 0;

if (cd_procedimento_p > 0) and (ie_origem_proced_p > 0) then
	select 	count(*)
	into STRICT	qt_pacote_w
	from 	pacote
	where	cd_proced_pacote = cd_procedimento_p
	and 	ie_origem_proced = ie_origem_proced_p
	and 	cd_convenio	 = cd_convenio_p
	and 	cd_estabelecimento = cd_estabelecimento_p
	and 	ie_situacao = 'A';
end if;

if (qt_pacote_w > 0) then
	ie_pacote_w:= 'S';
else
	ie_pacote_w:= 'N';
end if;

return	ie_pacote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
