CREATE OR REPLACE FUNCTION obter_se_pac_agenda_acesso_pep ( nr_atendimento_p bigint, cd_medico_agenda_p text, nm_usuario_p text, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1);
ie_agendas_executadas_w	varchar(1);
qt_agendamentos_w	integer;
ie_executado_w		varchar(1);
			

BEGIN 
 
select	count(*) 
into STRICT	qt_agendamentos_w 
from	agenda_global_v 
where	nr_atendimento		= nr_atendimento_p 
and	cd_medico_agenda	= cd_medico_agenda_p 
and	dt_agenda		>= trunc(clock_timestamp(),'dd') 
and	ie_status_agenda  not in ('F','I','L','B','R','LF','PA','C');
 
 
if (qt_agendamentos_w > 0) then 
	ds_retorno_w	:= 'S';
else 
	ds_retorno_w	:= 'N';
end if;
 
return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
