CREATE OR REPLACE FUNCTION obter_se_pac_agenda ( cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1);
ie_agendas_executadas_w	varchar(1);
qt_agendamentos_w	integer;
ie_executado_w		varchar(1);
ie_agendas_passadas_w	varchar(1);
			

BEGIN 
 
ie_agendas_passadas_w := Obter_Param_Usuario(970, 1, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_agendas_passadas_w);
ie_agendas_executadas_w := Obter_Param_Usuario(970, 2, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_agendas_executadas_w);
 
if (ie_agendas_executadas_w	= 'S') then 
	ie_executado_w	:= 'X';
else 
	ie_executado_w	:= 'E';
end if;
 
if (ie_agendas_passadas_w	= 'S') then 
	select	count(*) 
	into STRICT	qt_agendamentos_w 
	from	agenda_global_v 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	ie_status_agenda  not in ('F','I','L','B','R','LF','PA','C', ie_executado_w);
else 
	select	count(*) 
	into STRICT	qt_agendamentos_w 
	from	agenda_global_v 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	dt_agenda		>= trunc(clock_timestamp(),'dd') 
	and	ie_status_agenda  not in ('F','I','L','B','R','LF','PA','C', ie_executado_w);
end if;
 
if (qt_agendamentos_w > 0) then 
	ds_retorno_w	:= 'S';
else 
	ds_retorno_w	:= 'N';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
