CREATE OR REPLACE FUNCTION obter_se_pac_agend_checkup ( nr_sequencia_p bigint, cd_pessoa_fisica_p text, dt_previsto_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_agendado_checkup_w		bigint;
qt_agendado_checkup_set_w	bigint;
ds_retorno_w				varchar(3) := 'N';
ie_possui_checkup_w			varchar(1);


BEGIN
ie_possui_checkup_w := obter_param_usuario(996, 36, obter_perfil_ativo, Obter_usuario_ativo, obter_estabelecimento_ativo, ie_possui_checkup_w);

if (cd_pessoa_Fisica_p IS NOT NULL AND cd_pessoa_Fisica_p::text <> '') then
	if (ie_possui_checkup_w = 'S') then
		select	count(*)
		into STRICT	qt_agendado_checkup_w
		from	checkup
		where	nr_sequencia		<> nr_sequencia_p
		and	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	dt_previsto_p	between(dt_previsto_p - 365) and (dt_previsto_p + 365)
		and	coalesce(dt_cancelamento::text, '') = '';
	elsif (ie_possui_checkup_w = 'A') then
		select	count(*)
		into STRICT	qt_agendado_checkup_w
        from	checkup
        where	cd_pessoa_fisica = cd_pessoa_fisica_p
        and		nr_sequencia <> nr_sequencia_p
        and		coalesce(dt_fim_interno::text, '') = ''
        and		coalesce(dt_fim_real::text, '') = ''
        and		coalesce(dt_cancelamento::text, '') = ''
        and		coalesce(nr_atendimento::text, '') = ''
        and		coalesce(dt_confirmacao::text, '') = '';
	end if;

	if (qt_agendado_checkup_w > 0) then
			ds_retorno_w := 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
