SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_pac_age_futura (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_futuras_w		varchar(1) := 'N';
dt_agenda_w		timestamp;
cd_pessoa_fisica_w	varchar(10);
cd_tipo_agenda_w	bigint;
qt_agenda_w		bigint;


BEGIN 
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then 
	select	b.dt_agenda, 
		b.cd_pessoa_fisica, 
		a.cd_tipo_agenda 
	into STRICT	dt_agenda_w, 
		cd_pessoa_fisica_w, 
		cd_tipo_agenda_w 
	from	agenda a, 
		agenda_consulta b 
	where	a.cd_agenda		= b.cd_agenda 
	and	b.nr_sequencia 	= nr_seq_agenda_p;
 
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '' AND dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then 
		select	coalesce(count(*),0) 
		into STRICT	qt_agenda_w 
		from	agenda a, 
			agenda_consulta b 
		where	a.cd_agenda 		= b.cd_agenda 
		and	a.cd_tipo_agenda	= cd_tipo_agenda_w 
		and	b.dt_agenda		> dt_agenda_w 
		and	b.cd_pessoa_fisica	= cd_pessoa_fisica_w 
		and	b.ie_status_agenda	<> 'C'  LIMIT 1;
	end if;
 
	if (coalesce(qt_agenda_w,0) > 0) then 
		ie_futuras_w	:= 'S';
	end if;
end if;
 
return	ie_futuras_w;		
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
