CREATE OR REPLACE FUNCTION obter_se_pac_alerta_alergia (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_alergia_w     varchar(1);
			

BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_alergia_w
from    	alerta_v
where   	cd_pessoa_alerta  = cd_pessoa_fisica_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	ie_tipo_alerta = 'A'
and 	ie_nega = 'N';


return	ie_alergia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
