CREATE OR REPLACE FUNCTION obter_se_pac_atendimento ( cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_atendimento_w	varchar(5);
nr_atendimento_w	bigint;


BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then 
 
	begin 
 
	select	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	cd_estabelecimento	= cd_estabelecimento_p;
 
	end;
	 
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
		ie_atendimento_w	:= wheb_mensagem_pck.get_texto(306949); -- Sim 
	else
		ie_atendimento_w	:= wheb_mensagem_pck.get_texto(306950); -- NÃ£o 
	end if;
 
end if;
 
return	ie_atendimento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
