CREATE OR REPLACE FUNCTION obter_se_pac_cirur_acesso_pep ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1):= 'N';



BEGIN

select	coalesce(max('S'),'N')
into STRICT	ds_retorno_w
from	cirurgia
where	nr_atendimento = nr_atendimento_p
and	((cd_medico_cirurgiao = cd_pessoa_fisica_p) or (cd_medico_anestesista = cd_pessoa_fisica_p));

if (ds_retorno_w = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	cirurgia a,
		cirurgia_participante b
	where	a.nr_cirurgia		= b.nr_cirurgia
	and	a.nr_atendimento 	= nr_atendimento_p
	and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
