CREATE OR REPLACE FUNCTION obter_se_pac_consulta_dia ( cd_agenda_p bigint, nr_seq_agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_agenda_w		bigint;
dt_agenda_w		timestamp;
cd_pessoa_fisica_w	varchar(10);
ie_agenda_dia_w		varchar(1);
ie_biometria_status_w	varchar(10);
nm_usuario_w		varchar(15);


BEGIN

ie_agenda_dia_w := 'N';
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	begin

	nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

	ie_biometria_status_w := obter_param_usuario(821, 423, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, ie_biometria_status_w);

	if (coalesce(ie_biometria_status_w,'N') = 'S') then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_agenda_dia_w
		from	agenda_consulta
		where	trunc(dt_agenda,'dd') = trunc(dt_agenda_p)
		and		cd_agenda = cd_agenda_p
		and		ie_status_agenda = 'O'
		and		(dt_consulta IS NOT NULL AND dt_consulta::text <> '')
		and		nr_sequencia <> nr_seq_agenda_p
		and		nm_usuario = nm_usuario_w;


	else

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_agenda_dia_w
		from	agenda_consulta
		where	trunc(dt_agenda,'dd') = trunc(dt_agenda_p)
		and	cd_agenda = cd_agenda_p
		and	ie_status_agenda = 'O'
		and	(dt_consulta IS NOT NULL AND dt_consulta::text <> '')
		and	nr_sequencia <> nr_seq_agenda_p;

	end if;

	end;
end if;

return ie_agenda_dia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
