CREATE OR REPLACE FUNCTION obter_se_pac_consulta_resid ( cd_agenda_p bigint, nr_seq_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_residente_p text) RETURNS varchar AS $body$
DECLARE


ie_agenda_dia_w					varchar(1);
nm_usuario_w					varchar(15);
qt_agenda_medico_w				bigint;
qt_agenda_residente_w			bigint;
qt_agenda_med_res_w				bigint;
qt_agenda_med_res_usuario_w		bigint;


BEGIN

ie_agenda_dia_w := 'N';

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (nm_usuario_residente_p IS NOT NULL AND nm_usuario_residente_p::text <> '')then
	begin

	nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_agenda_dia_w
	from	agenda_consulta
	where	trunc(dt_agenda,'dd') = trunc(dt_agenda_p)
	and		cd_agenda = cd_agenda_p
	and		ie_status_agenda = 'O'
	and		(dt_consulta IS NOT NULL AND dt_consulta::text <> '')
	and		nr_sequencia <> nr_seq_agenda_p
	and		nm_usuario = nm_usuario_w
	and		nm_usuario_residente = nm_usuario_residente_p;

	if (ie_agenda_dia_w = 'N') then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_agenda_dia_w
		from	agenda_consulta
		where	trunc(dt_agenda,'dd') = trunc(dt_agenda_p)
		and		cd_agenda = cd_agenda_p
		and		ie_status_agenda = 'O'
		and		(dt_consulta IS NOT NULL AND dt_consulta::text <> '')
		and		nr_sequencia <> nr_seq_agenda_p
		and		nm_usuario_residente = nm_usuario_w;

	end if;

	select	count(*)
	into STRICT	qt_agenda_medico_w
	from	agenda_consulta
	where	trunc(dt_agenda,'dd') = trunc(dt_agenda_p)
	and		cd_agenda = cd_agenda_p
	and		ie_status_agenda = 'O'
	and		(dt_consulta IS NOT NULL AND dt_consulta::text <> '')
	and		nr_sequencia <> nr_seq_agenda_p
	and		nm_usuario = nm_usuario_w;


	select	count(*)
	into STRICT	qt_agenda_residente_w
	from	agenda_consulta
	where	trunc(dt_agenda,'dd') = trunc(dt_agenda_p)
	and		cd_agenda = cd_agenda_p
	and		ie_status_agenda = 'O'
	and		(dt_consulta IS NOT NULL AND dt_consulta::text <> '')
	and		nr_sequencia <> nr_seq_agenda_p
	and		nm_usuario_residente = nm_usuario_residente_p;


	select	count(*)
	into STRICT	qt_agenda_med_res_w
	from	agenda_consulta
	where	trunc(dt_agenda,'dd') = trunc(dt_agenda_p)
	and		cd_agenda = cd_agenda_p
	and		ie_status_agenda = 'O'
	and		(dt_consulta IS NOT NULL AND dt_consulta::text <> '')
	and		nr_sequencia <> nr_seq_agenda_p
	and		nm_usuario = nm_usuario_w
	and		nm_usuario_residente = nm_usuario_residente_p;


	if	((qt_agenda_medico_w + qt_agenda_residente_w + qt_agenda_med_res_usuario_w - qt_agenda_med_res_w) > 2) then

		ie_agenda_dia_w := 'S';

	end if;

	end;
end if;

return ie_agenda_dia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
