CREATE OR REPLACE FUNCTION obter_se_pac_demarcado (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327113, null, wheb_usuario_pck.get_nr_seq_idioma);--Sim
expressao2_w	varchar(255) := obter_desc_expressao_idioma(327114, null, wheb_usuario_pck.get_nr_seq_idioma);--Não
expressao3_w	varchar(255) := obter_desc_expressao_idioma(500223, null, wheb_usuario_pck.get_nr_seq_idioma);--Não adequada
BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then

	select	max(coalesce(CASE WHEN 'S'=expressao1_w THEN 'N' WHEN 'S'=expressao2_w THEN 'NA'  ELSE expressao3_w END ,expressao2_w))
	into STRICT	ds_retorno_w
	from	pepo_confirm_demarcacao a
	where	a.nr_sequencia	=	(SELECT max(nr_sequencia)
								from	pepo_confirm_demarcacao
								where	nr_cirurgia = nr_cirurgia_p);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
