CREATE OR REPLACE FUNCTION obter_se_pac_disp_dia ( dt_referencia_p timestamp, nr_atendimento_p bigint, ie_classif_disp_niss_p text ) RETURNS varchar AS $body$
DECLARE



ie_utiliza_w	varchar(1);


BEGIN


select	coalesce(max('S'),'N')
into STRICT	ie_utiliza_w
from	dispositivo b,
	atend_pac_dispositivo a
where	a.nr_seq_dispositivo	= b.nr_sequencia
and	a.nr_atendimento	= nr_atendimento_p
and	dt_referencia_p between trunc(dt_instalacao,'dd') and trunc(coalesce(dt_retirada,clock_timestamp()),'dd') + 86399/86400
and	ie_classif_disp_niss	= ie_classif_disp_niss_p;

return	ie_utiliza_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
