CREATE OR REPLACE FUNCTION obter_se_pac_espera_hor_agenda ( cd_Agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w				varchar(1)	:= 'N';
qt_pac_espera_w				integer;
ie_lista_espera_w			varchar(1);
ie_forma_cons_lista_esp_w	varchar(1);
cd_especialidade_w			bigint;
cd_funcao_w					bigint;
			

BEGIN 
cd_funcao_w	:= wheb_usuario_pck.get_cd_funcao;
 
--Agenda de exames 
if (cd_funcao_w = 820) then 
	ie_forma_cons_lista_esp_w := Obter_Param_Usuario(820, 97, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_forma_cons_lista_esp_w);
elsif (cd_funcao_w = 821) then 
--Agenda de consultas 
	ie_forma_cons_lista_esp_w := Obter_Param_Usuario(821, 88, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_forma_cons_lista_esp_w);
end if;	
 
 
if (ie_forma_cons_lista_esp_w = 'E')then 
	 
	select	max(cd_especialidade) 
	into STRICT	cd_especialidade_w 
	from	agenda 
	where	cd_agenda = cd_agenda_p;
	 
end if;
 
select	max(coalesce(ie_lista_espera,'N')) 
into STRICT	ie_lista_espera_w 
from	agenda 
where	cd_agenda = cd_agenda_p;
 
if (ie_lista_espera_w = 'N') and (ie_forma_cons_lista_esp_w = 'S')then 
	select	count(*) 
	into STRICT	qt_pac_espera_w 
	from	agenda_lista_espera 
	where	cd_agenda	= cd_agenda_p 
	and		dt_desejada	= dt_agenda_p 
	and		ie_status_espera = 'A';
 
	if (qt_pac_espera_w	> 0) then 
		ds_Retorno_w	:= 'S';
	end if;
 
end if;	
 
if (ds_Retorno_w = 'N') and (ie_forma_cons_lista_esp_w = 'S')then 
	 
	select	count(*) 
	into STRICT	qt_pac_espera_w 
	from	agenda_lista_espera 
	where	cd_agenda	= cd_agenda_p 
	and		dt_agenda_p between DT_PERIODO_INICIAL and DT_PERIODO_FINAL 
	and		ie_status_espera = 'A';
 
	if (qt_pac_espera_w	> 0) then 
		ds_Retorno_w	:= 'S';
	end if;
end if;
	 
if (ds_Retorno_w = 'N') and (ie_forma_cons_lista_esp_w = 'E')then 
 
	select	count(*) 
	into STRICT	qt_pac_espera_w 
	from	agenda_lista_espera 
	where	coalesce(cd_agenda, cd_agenda_p)	= cd_agenda_p 
	and		cd_especialidade			= cd_especialidade_w 
	and		dt_agenda_p 				between DT_PERIODO_INICIAL and DT_PERIODO_FINAL 
	and		ie_status_espera 			= 'A';
 
	if (qt_pac_espera_w	> 0) then 
		ds_Retorno_w	:= 'S';
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
