CREATE OR REPLACE FUNCTION obter_se_pac_exige_sigilo ( cd_pessoa_fisica_p text, nr_classif_pf_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  		varchar(255);


BEGIN
ds_retorno_w := '';
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_classif_pf_p IS NOT NULL AND nr_classif_pf_p::text <> '') then

	select  max(ds_classificacao)
	into STRICT    ds_retorno_w
	from    pessoa_classif a,
		classif_pessoa b
	where   cd_estabelecimento =  wheb_usuario_pck.get_cd_estabelecimento
	and  	cd_pessoa_fisica   =  cd_pessoa_fisica_p
	and 	a.nr_seq_classif = b.nr_sequencia
	and 	to_char(nr_seq_classif)    =  nr_classif_pf_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
