CREATE OR REPLACE FUNCTION obter_se_pac_gravida ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ie_sexo_w	varchar(1);
ie_pac_gravida_w	varchar(1);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select	a.ie_sexo
	into STRICT	ie_sexo_w
	from	pessoa_fisica a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (ie_sexo_w = 'F') then
		begin
		select	max(a.ie_pac_gravida)
		into STRICT	ie_pac_gravida_w
		from	historico_saude_mulher a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

		select	CASE WHEN ie_pac_gravida_w='S' THEN obter_desc_expressao(719927)  ELSE obter_desc_expressao(327114) END
		into STRICT	ds_retorno_w
		;
		end;
	else
		ds_retorno_w := obter_desc_expressao(293950);
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
