CREATE OR REPLACE FUNCTION obter_se_pac_grupo_lib ( nr_seq_grupo_p bigint) RETURNS varchar AS $body$
DECLARE



qt_registro_lib_w	bigint;
cd_pessoa_fisica_w	varchar(10);
cd_perfil_w		integer;
cd_especialidade_w	integer;


BEGIN

select 	count(*)
into STRICT	qt_registro_lib_w
from	pac_grupo_lib
where	nr_seq_grupo	= 	nr_seq_grupo_p;

cd_pessoa_fisica_w := Obter_Dados_Usuario_Opcao(wheb_usuario_pck.get_nm_usuario,'C');
cd_especialidade_w := coalesce(wheb_usuario_pck.get_cd_especialidade,0);
cd_perfil_w:= obter_perfil_ativo;

if (qt_registro_lib_w	> 0) then
	select 	count(*)
	into STRICT	qt_registro_lib_w
	from	pac_grupo_lib
	where	nr_seq_grupo	= 	nr_seq_grupo_p
	and		coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w
	and 	coalesce(cd_pessoa_fisica,cd_pessoa_fisica_w) = cd_pessoa_fisica_w
	and		coalesce(cd_especialidade,cd_especialidade_w) = cd_especialidade_w;

	if (qt_registro_lib_w	= 0) then
		return 'N';
	end if;
end if;


return	'S';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
