CREATE OR REPLACE FUNCTION obter_se_pac_home_internado ( cd_pessoa_fisica_p text, dt_agendado_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_reg_w	bigint;				
		

BEGIN


select	count(*)
into STRICT	qt_reg_w
from    setor_atendimento c, 
	atendimento_paciente b, 
	unidade_atendimento a 
where   a.nr_atendimento        = b.nr_atendimento 
and     a.cd_setor_atendimento  = c.cd_setor_atendimento 
and     c.cd_classif_setor      in (3,4,8) 
and     b.cd_pessoa_fisica      = cd_pessoa_fisica_p
and	((trunc(b.dt_alta) > dt_agendado_p) or (coalesce(b.dt_alta::text, '') = ''));

return qt_reg_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
