CREATE OR REPLACE FUNCTION obter_se_pac_isolamento_pf (cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


  ds_retorno_w       varchar(1);
  cd_pessoa_fisica_w atendimento_paciente.cd_pessoa_fisica%type;

BEGIN
  ds_retorno_w       := 'N';
  cd_pessoa_fisica_w := to_char(cd_pessoa_fisica_p);

  if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
    select coalesce(max(ie_paciente_isolado), 'N')
      into STRICT ds_retorno_w
      from atendimento_paciente
     where cd_pessoa_fisica = cd_pessoa_fisica_w
       and coalesce(dt_alta::text, '') = '';
  end if;

  return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
