CREATE OR REPLACE FUNCTION obter_se_pac_outra_lista (cd_pessoa_fisica_p text, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w	varchar(1);	
ie_lista_espera_w varchar(1);		
 

BEGIN 
 
 
select	max(coalesce(ie_lista_espera,'N')) 
into STRICT	ie_lista_espera_w 
from	agenda 
where	cd_agenda = cd_agenda_p;
 
if (ie_lista_espera_w = 'N') then 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ds_retorno_w 
	from	agenda_lista_espera 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	cd_agenda		<> cd_agenda_p 
	and	ie_status_espera	= 'A';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
