CREATE OR REPLACE FUNCTION obter_se_pac_pertence_atend ( cd_pessoa_fisica_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

		 
		ie_atend_pertence_w varchar(1) := 'N';

BEGIN
	 
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
			select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END  
			into STRICT	ie_atend_pertence_w 
			from	atendimento_paciente 
			where	nr_atendimento = nr_atendimento_p 
			and		cd_pessoa_fisica = cd_pessoa_fisica_p;
	end if;
	 
	return ie_atend_pertence_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
