CREATE OR REPLACE FUNCTION obter_se_pac_problema (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_w	varchar(1) := 'N';




BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select coalesce(max('S'),'N') ie_existe
	into STRICT   ie_existe_w
	from lista_problema_pac a
	where a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and coalesce(a.dt_fim::text, '') = ''
	and coalesce(a.ie_situacao,'A') = 'A'
	and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and coalesce(a.dt_inativacao::text, '') = '';
end if;

return ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
