SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_pac_ps (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possui_w			varchar(2) := 'N';
cd_setor_atendimento_w		integer;
cd_classif_setor_w		varchar(2);
ie_extensao_ps_w		varchar(1);


BEGIN

ie_extensao_ps_w := obter_param_usuario(950, 181, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, coalesce(wheb_usuario_pck.get_cd_estabelecimento, 1), ie_extensao_ps_w);

select	max(coalesce(obter_unidade_atendimento(nr_atendimento,'IA','CS'),
		obter_unidade_atendimento(nr_atendimento,'A','CS')))
into STRICT	cd_setor_atendimento_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;

select	max(cd_classif_setor)
into STRICT	cd_classif_setor_w
from	setor_atendimento
where	cd_setor_atendimento	= cd_setor_atendimento_w;

if (cd_classif_setor_w	<> '1')	then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_w
	from	prescr_medica a,
			setor_atendimento b
	where	a.nr_atendimento = nr_atendimento_p
	and		a.dt_validade_prescr > clock_timestamp()
	and		a.cd_setor_atendimento = b.cd_setor_atendimento
	and		coalesce(a.dt_suspensao::text, '') = ''
	and		(coalesce(a.dt_liberacao, a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, a.dt_liberacao_medico))::text <> '')
	and		b.cd_classif_setor = '1'
	and (ie_extensao_ps_w <> 'C' or
			not exists (SELECT	1
						from	prescr_medica  a,
								setor_atendimento b
						where	a.nr_atendimento = nr_atendimento_p
						and		a.dt_validade_prescr > clock_timestamp()
						and		a.cd_setor_atendimento = b.cd_setor_atendimento
						and		coalesce(a.dt_suspensao::text, '') = ''
						and		(coalesce(a.dt_liberacao, a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, a.dt_liberacao_medico))::text <> '')
						and		b.cd_classif_setor <> '1'));

end if;

return	ie_possui_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
