CREATE OR REPLACE FUNCTION obter_se_pac_reavaliacao ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
								 
dt_inicio_reav_w				timestamp;
dt_fim_reav_w				timestamp;
															

BEGIN 
 
 
Select 	max(dt_inicio_reavaliacao), 
	max(dt_fim_reavaliacao) 
into STRICT	dt_inicio_reav_w, 
	dt_fim_reav_w 
from	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
 
 
If (dt_inicio_reav_w IS NOT NULL AND dt_inicio_reav_w::text <> '') then 
	begin 
		if (dt_inicio_reav_w < clock_timestamp()) then 
			if (dt_fim_reav_w IS NOT NULL AND dt_fim_reav_w::text <> '') then 
				if (dt_fim_reav_w > clock_timestamp()) then 
					return 'S';
				else 
					return 'N';
				end if;
			else 
				return 'S';
			end if;
		else 
			return 'N';
		end if;
	end;
end if;
 
return 'N';
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
