CREATE OR REPLACE FUNCTION obter_se_pac_regra_evento ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_proc_mat_p text) RETURNS char AS $body$
DECLARE


ie_regra_w		char(1) := 'N';
			

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	if (ie_proc_mat_p	= 'P') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_regra_w
		from	prescr_procedimento a where		a.nr_prescricao = nr_prescricao_p
		and		obter_se_proc_rec_alerta(a.nr_seq_proc_interno,nr_sequencia_p,a.cd_procedimento,a.ie_origem_proced, a.nr_seq_exame) = 'S' LIMIT 1;
	elsif (ie_proc_mat_p	= 'M') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_regra_w
		from	prescr_material a where		nr_prescricao = nr_prescricao_p
		and		obter_se_mat_rec_alerta(a.cd_material,nr_sequencia_p) = 'S' LIMIT 1;
	elsif (ie_proc_mat_p	= 'D') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_regra_w
		from	prescr_dieta a where		nr_prescricao = nr_prescricao_p
		and		obter_se_dieta_rec_alerta(a.cd_dieta,nr_sequencia_p) = 'S' LIMIT 1;
	elsif (ie_proc_mat_p	= 'R') then
		select 	coalesce(max('S'),'N')
		into STRICT 	ie_regra_w
		from 	prescr_recomendacao a where 	nr_prescricao = nr_prescricao_p
		and 	obter_se_regra_rec_alerta(a.cd_recomendacao,nr_sequencia_p) = 'S' LIMIT 1;
	end if;
end if;

return	ie_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
