CREATE OR REPLACE FUNCTION obter_se_pagador_plano_saude (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


qt_pagador_w			bigint;


BEGIN

select   count(*)
into STRICT qt_pagador_w
from     pessoa_fisica a, 
         pls_contrato_pagador b,
			pls_segurado c
where    a.cd_pessoa_fisica = b.cd_pessoa_fisica 
and (coalesce(b.dt_rescisao::text, '') = '' or b.dt_rescisao > clock_timestamp()) 
and      a.cd_pessoa_fisica = cd_pessoa_fisica_p
and		c.cd_pessoa_fisica = b.cd_pessoa_fisica
and		c.ie_tipo_segurado <> 'B';

return qt_pagador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
