SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_pagamento_liberado (nr_documento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_min_usu_lib_w	bigint;
cont_w		bigint;
ie_liberado_w	varchar(15);
cont_usuarios_w	bigint;

/*

ie_opcao_p

B - BorderÃ´ pagamento
P- Pagamento escritural
T- Titulo a pagar

*/
BEGIN

ie_liberado_w	:= 'S';

if (ie_opcao_p	= 'B') then

	select	coalesce(a.qt_min_usuario_lib,0)
	into STRICT	qt_min_usu_lib_w
	from	bordero_pagamento a
	where	a.nr_bordero		= nr_documento_p;

	select	count(*)
	into STRICT	cont_usuarios_w
	from	conta_pagar_lib a
	where	a.nr_bordero		= nr_documento_p;

	if (cont_usuarios_w > 0) then

		select	count(*)
		into STRICT	cont_w
		from	conta_pagar_lib a
		where	a.nr_bordero		= nr_documento_p
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

		if (cont_w < qt_min_usu_lib_w) then
			ie_liberado_w	:= 'N';
		end if;
	end if;

elsif (ie_opcao_p	= 'T') then

	select	coalesce(a.qt_min_usuario_lib,0)
	into STRICT	qt_min_usu_lib_w
	from	titulo_pagar a
	where	a.nr_titulo		= nr_documento_p;

	select	count(*)
	into STRICT	cont_usuarios_w
	from	conta_pagar_lib a
	where	a.nr_titulo		= nr_documento_p;

	if (cont_usuarios_w > 0) then

		select	count(*)
		into STRICT	cont_w
		from	conta_pagar_lib a
		where	a.nr_titulo		= nr_documento_p
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

		if (cont_w < qt_min_usu_lib_w) then
			ie_liberado_w	:= 'N';
		end if;
	end if;

else
	select	a.qt_min_usuario_lib
	into STRICT	qt_min_usu_lib_w
	from	banco_escritural a
	where	a.nr_sequencia		= nr_documento_p;

	select	count(*)
	into STRICT	cont_usuarios_w
	from	conta_pagar_lib a
	where	a.nr_seq_banco_escrit	= nr_documento_p;

	if (cont_usuarios_w > 0) then

		select	count(*)
		into STRICT	cont_w
		from	conta_pagar_lib a
		where	a.nr_seq_banco_escrit	= nr_documento_p
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

		if (cont_w < qt_min_usu_lib_w) then
			ie_liberado_w	:= 'N';
		end if;
	end if;
end if;

return	ie_liberado_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_pagamento_liberado (nr_documento_p bigint, ie_opcao_p text) FROM PUBLIC;

