CREATE OR REPLACE FUNCTION obter_se_painel_agenda_perfil ( cd_perfil_p bigint, cd_agenda_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
qt_agendas_w	bigint  := 0;


BEGIN

select 	count(*)
into STRICT	qt_agendas_w
from	regra_lib_agenda_painel
where	cd_agenda = cd_agenda_p;

if (qt_agendas_w > 0) then

	select 	count(*)
	into STRICT	qt_agendas_w
	from	regra_lib_agenda_painel
	where	cd_perfil = cd_perfil_p
	and	cd_agenda = cd_agenda_p;


	if (qt_agendas_w > 0) then

		ds_retorno_w := 'S';

	end if;

else
	ds_retorno_w := 'S';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
