CREATE OR REPLACE FUNCTION obter_se_param_pais ( cd_funcao_p bigint, nr_seq_param_p bigint, cd_pais_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(3);


BEGIN

